import {
    Center,
    Text,
    Heading,
    VStack,
    Button,
    Input,
    HStack,
    Container,
    SimpleGrid,
    Box,
    Image,
    Spinner,
} from "@chakra-ui/react";

import { useState, useEffect } from "react";
import { ChakraProvider } from "@chakra-ui/react";

function App() {
    const[isSelected, setIsSelected] = useState(false);
    const [selectedFile, setSelectedFile] = useState(null);
    const [allVideos, setAllVideos] = useState([]);
    const [uploadSuccessful, setUploadSuccessful] = useState(false);
    const [showSpinner, setShowSpinner] = useState(false);

    useEffect(() => {
        fetch("http://127.0.0.1:8000/videos")
            .then((response) => response.json())
            .then((data) => {
                setAllVideos(data);
            });
    }, [uploadSuccessful]);

    const onInputChange = (e) => {
        setIsSelected(true);
        setSelectedFile(e.target.files[0]);
    };

    const onFileUpload = (e) => {
        setShowSpinner(true)
    }

    return (
        <ChakraProvider>
            <Center bg="black" color="white" padding={8}>
                <VStack spacing={7}>
                    <Heading> Votre galerie vidéos</Heading>
                    <Text>Aperçu de toutes vos vidéos téléchargées !</Text>
                    <HStack>
                        <input type="file" onChange={onInputChange} onClick={null}></input>
                        <Button 
                        size="lg" 
                        colorScheme="red" 
                        isDisabled={!isSelected} 
                        onClick={onFileUpload}
                        >
                            Télécharger une vidéo
                        </Button>
                    </HStack>
                    <Heading>Vos vidéos</Heading>
                    <SimpleGrid columns={3} spacing={8}>
                        {allVideos.length !== 0 &&
                            allVideos.map(video => {
                                return (
                                    <video
                                    src={video["video_url"]}
                                    autoPlay
                                    controls
                                    loop
                                    preload="auto"
                                    ></video>
                                )
                            })}
                    </SimpleGrid>
                </VStack>
            </Center>
        </ChakraProvider>
    );
}

export default App;
